Iteration Skill Damage 
<Skill Tier: 1>
<Ranged>
<Damage Formula>
// Default the number of times it's been casted to 0
var total = user._totalSpellsCasted || 0;
// The multiplier used based on the number of times it's been used.
var bonusMultiplier = total * 0.50;
// The damage formula with reference to the multiplier bonus
value = user.mat * (1 + bonusMultiplier);
</Damage Formula>

// The passive state linked to this skill
<Passive State: 58>

Imperial Highblade Shock
<Damage Formula>
// Check if the damage has already been calculated
if (this._calculatedBaseDmg) {
  // Set the damage to the cached amount
  value = this._calculatedBaseDmg;
// Otherwise
} else {
  // Get the total number of enemies
  var totalEnemies = target.friendsUnit().aliveMembers().length;
  // If there is 1 enemy total
  if (totalEnemies === 1) {
    // Use this formula
    this._calculatedBaseDmg = user.atk * 16;
  // If there are 2 enemies in total
  } else if (totalEnemies === 2) {
    // Use this formula
    this._calculatedBaseDmg = user.atk * 8;
  // If there are 3 enemies in total
  } else if (totalEnemies === 3) {
    // Use this formula
    this._calculatedBaseDmg = user.atk * 4;
  // if there are 4 or more
  } else {
    // Use this formula
    this._calculatedBaseDmg = user.atk * 2;
  }
  // Set the value equal to the calculated amount
  value = this._calculatedBaseDmg;
}
</Damage Formula>

Entrust
// Make the user require at least 1 TP to transfer
<Custom Requirement>
value = user.tp >= 1;
</Custom Requirement>

// Make the user unable to select self
<Select Conditions>
Not User
</Select Conditions>

<After Eval>
// Set the target's TP to the user's current TP
target.setTp(user.tp);
// Set the user's TP to 0
user.setTp(0);
</After Eval>
