HP reaches 0.
<Custom Apply Effect>
if (target.isActor()) {
  var ring = $dataArmors[13];
  if (target.hasArmor(ring)) {
    target.discardEquip(ring);
    target.startAnimation(42);
    var hp = Math.floor(target.mhp * 0.25);
    target.gainHp(hp);
    target.startDamagePopup();
  }
}
</Custom Apply Effect>

"Magic Guard"
<Custom React Effect>
if (value > 0 && this.isHpEffect()) {
  var mpDmg = Math.floor(value * 0.85);
  mpDmg = Math.min(mpDmg, target.mp);
  target.gainMp(-mpDmg);
  value -= mpDmg;
  if (target.mp === 0) {
    target.removeState(16);
  }
}
</Custom React Effect>
